

public class PatternL {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int M=4;
		
		for (int i=1;i<=M;i++)
		{
			if (i!=M)
			{
				System.out.println("L");
			}
			else
			{
				for (int j=1;j<=M;j++)
				{
					System.out.print("L");	
				}
				
			}
		}
		
		}

	}
